x[name='"+ $(this).attr("name") + "']";			// if($(this).is(':checked')){				// $(group).not($(this)).attr("checked",false);			// }			var values = new Array();			$.each($("input[name='EA[]']:checked"), function() {				values.push($(this).val());			});			values = values.join(',');			load_data_ajax(values);			var len = $("input[name='EA[]']:checked").length;			var result = len*150000;			$("input[name='cost']").val(result);			$(".cost").text(result);		}else if($(this).attr("name") == 'AP[]'){			var values = new Array();			$.each($("input[name='AP[]']:checked"), function() {				if($(this).val()=='Enginer 1' || $(this).val()=='Enginer 2'){					$(this).val('Engine');				}				values.push($(this).val());			});			values = values.join(',');			load_data_ajax(values);			var len = $("input[name='AP[]']:checked").length;		}	});	$('#ap_list').click(function(){		if($(this).val()=='AP Double'){			var result = '300000';			$("input[name='cost']").val(result);			var result = '300.000';			$(".cost").text(result);			$('#ap_single').hide();			$('#ap_double').show();		}else if($(this).val()=='AP Single'){			var result = '150000';			$("input[name='cost']").val(result);			var result = '150.000';			$(".cost").text(result);			$('#ap_double').hide();			$('#ap_single').show();		}else{			$('#ap_double').hide();			$('#ap_single').hide();		}	});	$('#test').click(function(){		if($(this).val()=='AP'){			$("#container").hide();			$("input[name='EA[]']").removeAttr('checked');			$("input[name='AP[]']").removeAttr('checked');			// $(".cost").text('0');			$("#EA").hide();			$("#ap_double").show();		}else if($(this).val()=='EA'){			var result = '150000';			$("input[name='cost']").val(result);			var result = '150.000';			$(".cost").text(result);			$('#ap_double').hide();			$('#ap_single').hide();			$("#container").show();			var container = $('#container');			var data = "<option value=''>Pilih Rating</option>";			container.html(data);			$("input[name='EA[]']").removeAttr('checked');			$("input[name='AP[]']").removeAttr('checked');			// $(".cost").text('0');			$("#AP").hide();			$("#EA").show();		}else{			var result = '0';			$("input[name='cost']").val(result);			var result = '0';			$(".cost").text(result);			var container = $('#container');			var data = "<option value=''>Pilih Rating</option>";			container.html(data);			// $(".cost").text('0');			$("#AP").hide();			$("#EA").hide();			$('#ap_double').hide();			$('#ap_single').hide();		}	});	$('#ste_book1').change(function(){		check = $('#ste_book1').is(":checked");		check2 = $('#ste_book2').is(":checked");		if(check) {			$('#log11').attr('disabled', 'disabled');			$('#log12').attr('disabled', 'disabled');			$('#log21').attr('disabled', 'disabled');			$('#log22').attr('disabled', 'disabled');		} else if (check2) {			$('#log11').attr('disabled', 'disabled');			$('#log12').attr('disabled', 'disabled');			$('#log21').attr('disabled', 'disabled');			$('#log22').attr('disabled', 'disabled');		} else {			$('#log11').removeAttr('disabled');			$('#log12').removeAttr('disabled');			$('#log21').removeAttr('disabled');			$('#log22').removeAttr('disabled');		}	});		$('#ste_book2').change(function(){		check = $('#ste_book1').is(":checked");		check2 = $('#ste_book2').is(":checked");		if(check) {			$('#log11').attr('disabled', 'disabled');			$('#log12').attr('disabled', 'disabled');			$('#log21').attr('disabled', 'disabled');			$('#log22').attr('disabled', 'disabled');		} else if (check2) {			$('#log11').attr('disabled', 'disabled');			$('#log12').attr('disabled', 'disabled');			$('#log21').attr('disabled', 'disabled');			$('#log22').attr('disabled', 'disabled');		} else {			$('#log11').removeAttr('disabled');			$('#log12').removeAttr('disabled');			$('#log21').removeAttr('disabled');			$('#log22').removeAttr('disabled');		}	});	$('#log11').change(function(){		check = $('#log11').is(":checked");		check2 = $('