e if (check4) {			$('#ste_book1').attr('disabled', 'disabled');			$('#ste_book2').attr('disabled', 'disabled');		} else {			$('#ste_book1').removeAttr('disabled');			$('#ste_book2').removeAttr('disabled');		}	});	$('#log22').change(function(){		check = $('#log11').is(":checked");		check2 = $('#log12').is(":checked");		check3 = $('#log21').is(":checked");		check4 = $('#log22').is(":checked");		if(check) {			$('#ste_book1').attr('disabled', 'disabled');			$('#ste_book2').attr('disabled', 'disabled');		} else if (check2) {			$('#ste_book1').attr('disabled', 'disabled');			$('#ste_book2').attr('disabled', 'disabled');		} else if (check3) {			$('#ste_book1').attr('disabled', 'disabled');			$('#ste_book2').attr('disabled', 'disabled');		} else if (check4) {			$('#ste_book1').attr('disabled', 'disabled');			$('#ste_book2').attr('disabled', 'disabled');		} else {			$('#ste_book1').removeAttr('disabled');			$('#ste_book2').removeAttr('disabled');		}	});</script>